// tasksReducer.js
import { types } from "./types";

const initialTaskState = [];

export default function tasksReducer(state = initialTaskState, action) {
    switch (action.type) {
        case types.ADD_TASK:
            return [...state, action.payload]; // Добавляем новую задачу в массив

        case types.TOGGLE_TASK:
            return state.map((task) =>
                task.id === action.payload ? { ...task, completed: !task.completed } : task
            );

        case types.REMOVE_TASK:
            return state.filter((task) => task.id !== action.payload);

        case types.UPDATE_TASK:
            return state.map((task) =>
                task.id === action.payload.taskId ? { ...task, ...action.payload.newData } : task
            );

        default:
            return state;
    }
}
