import { useDispatch, useSelector } from "react-redux";
import { toggleTask, removeTask, updateTask } from "../redux/reducers/actions";

const TaskList = ({ filter }) => {
    const tasks = useSelector((state) =>
        filter
            ? state.tasksReducer.filter((task) => task.category === filter)
            : state.tasksReducer
    );
    const dispatch = useDispatch();

    const handleRemove = (taskId) => {
        dispatch(removeTask(taskId));
    };

    const handleUpdate = (taskId, newData) => {
        dispatch(updateTask(taskId, newData));
    };

    return (
        <ul className="task-list">
            {tasks.map((task) => (
                <li
                    key={task.id}
                    className={`task-item ${task.completed ? 'completed' : ''}`}
                    onClick={() => dispatch(toggleTask(task.id))}
                >
                    {task.text} <span>({task.category})</span>
                    <button onClick={() => handleRemove(task.id)}>Удалить</button>
                    <button onClick={() => handleUpdate(task.id, { text: "Обновленный текст задачи" })}>
                        Обновить
                    </button>
                </li>
            ))}
        </ul>
    );
};

export default TaskList;
