// src/redux/reducers/tasksReducer.js
import {types} from "./types";

// Начальное состояние для задач
const initialTaskState = []

export default function tasksReducer(state = initialTaskState, action) {
    switch (action.type) {
        case types.ADD_TASK:
            return [...state, action.payload]; // Добавляем задачу в массив
        default:
            return state; // Возвращаем текущее состояние, если действие не найдено
    }
}

// Начальное состояние для категорий
const initialCategoriesState = ['Work', 'Personal', 'Other'];

// Исправление редьюсера для категорий
export const categoriesReducer = (state = initialCategoriesState, action) => {
    switch (action.type) {
        // Пример: добавление категории
        case types.ADD_CATEGORY:
            return [...state, action.payload];
        // Пример: удаление категории
        case types.REMOVE_CATEGORY:
            return state.filter(category => category !== action.payload);
        default:
            return state; // Возвращаем состояние по умолчанию, если действие не найдено
    }
};
