import {types} from "./types";


const initialTaskState = []

export default function tasksReducer(state = initialTaskState, action) {
    switch (action.type) {
        case types.ADD_TASK:
            return [...state, action.payload];
        default:
            return state;
    }
}


const initialCategoriesState = ['Work', 'Personal', 'Other'];


export const categoriesReducer = (state = initialCategoriesState, action) => {
    switch (action.type) {

        case types.ADD_TASK:
            return [...state, action.payload];

        case types.TOGGLE_TASK:
            return state.map(task => category !== action.payload);
        default:
            return state;
    }
};
